import React, { useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const MapApi = () => {
  useEffect(() => {
    const loadKakaoMapScript = () => {
      const script = document.createElement("script");
      script.async = true;
      script.src =
        "https://dapi.kakao.com/v2/maps/sdk.js?appkey=89730aca4ca56bd725e48019977366cc&autoload=false";
      document.head.appendChild(script);

      script.onload = () => {
        window.kakao.maps.load(() => {
          initializeMap();
        });
      };
    };

    const initializeMap = () => {
      const mapContainer = document.getElementById("map");
      const mapOption = {
        center: new window.kakao.maps.LatLng(33.450701, 126.570667),
        level: 4,
      };
      const map = new window.kakao.maps.Map(mapContainer, mapOption);

      const geocoder = new window.kakao.maps.services.Geocoder();

      geocoder.addressSearch(
        "경기 성남시 분당구 판교역로 235 에이치스퀘어",
        function (result, status) {
          if (status === window.kakao.maps.services.Status.OK) {
            const coords = new window.kakao.maps.LatLng(
              result[0].y,
              result[0].x
            );
            const message =
              "latlng : new window.kakao.maps.LatLng(" +
              result[0].y +
              ", " +
              result[0].x +
              ")";

            const resultDiv = document.getElementById("clickLatlng");
            resultDiv.innerHTML = message;

            const marker = new window.kakao.maps.Marker({
              map: map,
              position: coords,
            });

            const infowindow = new window.kakao.maps.InfoWindow({
              content:
                '<div style="width:150px;text-align:center;padding:6px 0;">장소</div>',
            });
            infowindow.open(map, marker);

            map.setCenter(coords);
          }
        }
      );
    };

    loadKakaoMapScript();
  }, []);

  return <div id="map" style={{ width: "100%", height: "350px" }}></div>;
};

export default MapApi;
